mapper.select:
select
from contents where deleted = #{deleted,jdbcType=BIT} and active = #{active,jdbcType=BIT} and id in
#{id}

select
date_format(from_unixtime(created), '%Y年%m月') as date_str, count(0) as count
from contents
where type = #{type,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
group by date_str

SELECT

FROM contents
WHERE type = #{type,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
and created
  between #{start,jdbcType=INTEGER} and #{end, jdbcType=INTEGER}
